import React, { useEffect, useState } from 'react'
import {useHistory} from 'react-router-dom'
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import EditorToolbar, { modules, formats } from '../../EditorToolbar';
//"../EditorToolbar"
import Modal from '@material-ui/core/Modal';

import axiosInstance from '../../AuthComp/AxiosInstance'
import {BaseUrl} from '../BaseUrl'
const UpdateN = (props) => {
    const history = useHistory();
    const [open, setOpen] = React.useState(props.show);
    const [note, setNotes] = useState([])
    const handleClose = () => {
        setOpen(false);
      };

    const getNotes = async () => {
        const data = await fetch(`${BaseUrl}view-notes${history.location.pathname}`)
        const notes = await data.json()
        setNotes(notes)
        console.log(notes)
    }
    useEffect(() => { getNotes()})  
    const sendNotes = () => {
        axiosInstance.post(`${BaseUrl}create-note`, {

        }).then((res) => {console.log(res); history.push('/notes')})
    }

    return (
        <div>
          <div style={{ display: 'block', padding: 30 }} className='shadow p-3 mb-5 bg-white rounded'>
            <Modal
                onClose={handleClose}
                open={open}
                style={{ position: 'absolute', border: "1px solid", boxShadow: '3px #888888', height:"80%", width: "70%", margin: 'auto',backgroundColor : "white"}}>
                <div className='container shadow p-3 mb-5 bg-white rounded' style={{"height" : "100%"}}>
                <h2 style={{"textAlign" : "center", "fontFamily" : "serif"}}>Update your Answer</h2>
                <br/>
            <form onSubmit={(e) => {e.preventDefault(); }}>

                <div className="form-group form-group-lg">
                    <label style={{"fontSize" : "10px"}} htmlFor="exampleInputPassword1">Title</label>
                    <input type="text" name='notes_title' style={{"height" : "30px"}}  className="form-control input-lg" id="exampleInputPassword1" placeholder="Note Title"/>
                </div>
                </form>
                <br/>
                <h5>Notes Content</h5>
{/* value={} onChange={} */}
                <EditorToolbar className='Editor-1' toolbarId={'t9'}/>
                    <ReactQuill 
                    theme="snow"
                    modules={modules('t9')}
                    formats={formats}
                    style={{"height" : "45%"}}

                    />
                    <br/>
                <button className='btn btn-outline-primary' style={{"width" : "50%"}} onClick={sendNotes}>Create</button>
                <button className='btn btn-outline-danger' style={{"width" : "50%"}} onClick={handleClose}>Cancel</button>
                </div>

            </Modal>
    </div>  
        </div>
    )
}

export default UpdateN
