import React, { useState } from 'react'
import './signup.css'
import {Link} from 'react-router-dom'
const styles =  {"minHeight": "100vh",
"display": "flex",
"justifyContent": "center",
"alignItems": "center",
"backgroundColor": "#eee",
"width" : "100%"}

//Here by using useState true and false, we can have this sliding functionality here
const Login = () =>  {
    	const[inputtext,setinputtext]=useState({email:"", password:""});

		const[warnemail,setwarnemail]=useState(false);
		const[warnpassword,setwarnpassword]=useState(false);

		const[eye,seteye]=useState(true);
		const[password,setpassword]=useState("password");
		const[type,settype]=useState(false);

		const inputEvent=(event)=>{
		const name=event.target.name;
		const value=event.target.value;
		setinputtext((lastValue)=>{
		return{
		...lastValue,
		[name]:value}});
	}
		const submitForm=(e) => 
		{
			e.preventDefault();
				setwarnemail(false);
				setwarnpassword(false);
		if(inputtext.email==="")
		{
			setwarnemail(true);
		}
		else if(inputtext.password===""){
		setwarnpassword(true);
		}
		else{
		alert("form submitted");
		}

		}

		const Eye=()=>{
		if(password==="password"){
		setpassword("text");
		seteye(false);
		settype(true);
		}
		else{
		setpassword("password");
		seteye(true);
		settype(false);
		}
		}


		return(
		<>
			<div className="container" style={styles}>
				<div className="card" style={{"height": "400px",	"width": "400px", "backgroundColor": "#fff", "position": "relative", "boxShadow": "0 15px 30px rgba(0, 0, 0, 0.1)",
    "fontFamily": "'Poppins', sans-serif",
    "borderRadius": "10px",
    "padding": "20px"}}>

					<div className="text" id="textField1">
					<br/>
						<h3>Welcome Back</h3>
						<br/>
						<p>Enter your credentials to access your account.</p>
						<br/>
					</div>
					<form onSubmit={submitForm}>
						<div className="input-text">
							<input type="text" id="textField" className={` ${warnemail ? "warning" : "" }`} placeholder="Enter your email" value={inputtext.email} onChange={inputEvent} name="email" />


						</div>
						<div className="input-text" id="inputText">
							<input type={password} id="passwordField" className={` ${warnpassword ? "warning" : "" } ${type ? "type_password" : "" }`} placeholder="Enter your password" value={inputtext.password} onChange={inputEvent} name="password" />

							<i onClick={Eye} className={`fa ${eye ? "fa-eye-slash" : "fa-eye" }`}></i>
						</div>
						<div className="buttons" id="Btn">
							<button id="Btnn" type="submit">Sign in</button>
						</div>
						<div className="forgot">
							<p>Don't have any Account? <Link style={{textColor :"black"}} to="/signup">Signup Here</Link></p>
						</div>
					</form>
				</div>
			</div>

		</>

		);

}

export default Login
