import React, { useEffect, useState } from 'react'
import {useHistory} from 'react-router-dom'
import Navbar from './Navbar';
import Error from './Error';
import 'quill/dist/quill.bubble.css'
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import EditorToolbar, { modules, formats } from "../EditorToolbar";
import UpdateQ from './UpdateQ';


const QnA = () => {
    const BaseUrl = `http://127.0.0.1:8000`
    const history = useHistory();
    const [question, setQuestion] = useState([])
    const [clicked, setclicked] = useState({clicked4ans : false, clicked4update : false});
    useEffect(() => {
        fetch(`${BaseUrl}${history.location.pathname}`)
        .then((data)=> data.json())
        .then((response)=> { console.log(response); setQuestion(response)}); 
    }, [setQuestion])




    return (
        <>
        <Navbar/>
        {typeof question[0] === "undefined" ? <>
            <Error/>
        </> : question[1].length !== 0 ? <div>
            <h4>
                {question.map((item, index)=> {
                    return(<>
                        <div key={index}>
                        <>
                        <h2>{(item.user === (JSON.parse(atob(localStorage.getItem('access_token').split('.')[1])).user_id))}</h2>
                        <h4>{item.question}</h4>
                        <p style={{color : "#808080"}}>{item.question_date}</p>
                        {item.question !== undefined && item.user === (JSON.parse(atob(localStorage.getItem('access_token').split('.')[1])).user_id)
                          ? 
                            <button type='button' className='btn btn-success' onClick={()=> { console.log("hey"); setclicked({clicked4update:true})}}>Update</button> : "" }
                        </> 

                        </div>
                    </>
                    )
                })}
            </h4>
            <hr/>
            {question[1].map((item, index) => {
                return(<>
                <div className='container'>
                    <p key={index}>{item.answer}</p>
                    <p>Answered on : {item.answered_date}</p>
                </div>
                </>
                )
            })}
        </div> : <>
        <div className='container' style={{width : "75%", marginTop:'5%'}}>
        <h4>
                {question.map((item, index)=> {
                    return(<>

                        <div key={index} style={{textAlign:"center"}}>
                            <h2>{item.question}</h2>
                            {item.question !== undefined && item.user === (JSON.parse(atob(localStorage.getItem('access_token').split('.')[1])).user_id)
                          ? 
                            <button type='button' className='btn btn-success' onClick={()=> <UpdateQ q={item.question} qd={item.question_data}/>}>Update</button> : "" }
                        </div>
                        {item.question !== undefined ? <>
                            <EditorToolbar toolbarId={'t1'}/>
                            <ReactQuill
                            theme="bubble"
                            readOnly={true}
                            value={item.question_data}
                            modules={modules('t1')}
                            formats={formats}/> </>
                        :<></>
                         }
                         <p>{item.user}</p>

                    </>)
                })}
            </h4>

            <hr/>

            <h5>No answer :-( .You haven't answered it yet</h5>
            <p>Can you answer it?</p>
            <button className='btn btn-success' onClick={() => setclicked(true)}>Answer it here</button>
            {clicked === true ? 
            <>

            <div className='container' id="Editor" style={{width : "75%"}}> 
                <EditorToolbar toolbarId={'t1'}/>
                <ReactQuill
                    theme="snow"
                    placeholder={"Write something awesome..."}
                    modules={modules('t1')}
                    formats={formats}/>
                    <button type='button' className='btn btn-outline-success'>Submit</button>
                    <button type='submit' className='btn btn-outline-danger' onClick={() => setclicked(false)}>Cancel</button>
                    </div>

            </>: <></>}
            </div>

        </>}
        </>
    )
}

export default QnA


