import React, { useEffect, useState } from 'react'
import { client, w3cwebsocket as W3CWebSocket } from "websocket";
import Button from '@material-ui/core/Button';
import CssBaseline from '@material-ui/core/CssBaseline';
import TextField from '@material-ui/core/TextField';
import Link from '@material-ui/core/Link';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import Card from '@material-ui/core/Card';
import CardHeader from '@material-ui/core/CardHeader';
import Paper from '@material-ui/core/Paper';
import Avatar from '@material-ui/core/Avatar';
import { useTheme } from '@material-ui/core/styles';

import { withStyles } from "@material-ui/core/styles";

const useStyles = theme => ({
  paper: {
    marginTop: theme.spacing(8),
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
  },
  avatar: {
    margin: theme.spacing(1),
    backgroundColor: theme.palette.secondary.main,
  },
  form: {
    width: '100%', // Fix IE 11 issue.
    marginTop: theme.spacing(1),
  },
  submit: {
    margin: theme.spacing(3, 0, 2),
  },
  root: {
    boxShadow: 'none',
  }
});

const Chat = () => {
  const theme = useTheme();

    const [chatState, setChatstate] = useState({
        messages : [],
        value : ' ',
        name : (JSON.parse(atob(localStorage.getItem('access_token').split('.')[1])).user_id),
        room : 'default'
    })
    const client = new W3CWebSocket('ws://127.0.0.1:8000/ws/chat/' + chatState.room);
    const [clicked, setclicked] = useState(false)


    const getData = () => {
        client.onmessage = (message) => {
            const datafromserver = JSON.parse(message.data);
            console.log(datafromserver);
            if(datafromserver){
                setChatstate({
                    messages : 
                    [...chatState.messages,
                    {
                        msg : datafromserver.message,
                        name : datafromserver.name
                    }
                ]})
            }
        }
    }
    console.log(chatState.messages)
    console.log(chatState.room)
    useEffect(() => {
        getData();
    }, [])
    const onButtonClicked = (e) => {

        client.send(JSON.stringify({
            type : "message",
            message : chatState.value,
            name : (JSON.parse(atob(localStorage.getItem('access_token').split('.')[1])).user_id)
        }));
        chatState.value = ''
        e.preventDefault();  
    }
    return (
        <>
          <Container component="main" maxWidth="xs">
              {clicked === true ?
              <div style={{ marginTop : 50}}>
                  Room name : {chatState.room}
                  <Paper style={{ height: 500, maxHeight: 500, overflow: 'auto', boxShadow: 'none', }}>
                  {/* {typeof chatState.messages !== 'undefined' ? chatState.messages.map((item, index) => {
                    return(<Card style={{boxShadow: 'none'}}>
                  <CardHeader
                    avatar={
                      <Avatar style={{margin: theme.spacing(1), backgroundColor: theme.palette.secondary.main}}>
                        R
                  </Avatar>
                    }
                    title={item.name}
                    subheader={item.message}
                  />
                </Card>)
                  } ) : <></>} */}
                  </Paper> 
                  <form style={{width: '100%', // Fix IE 11 issue.
      marginTop: theme.spacing(1)}}  onSubmit={onButtonClicked}>

    <label htmlFor="exampleInputEmail1">Messages</label>
    <input type="text" className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Message" 
    value={chatState.value}
    onChange={(e) => setChatstate({ value : e.target.value})}/>
              <Button
                type="submit"
                fullWidth
                variant="contained"
                color="primary"
                className='Button'
              >
                Start Chatting
                </Button>
            </form>  
              </div> 
              :<>
              <CssBaseline />
              <div style={{ marginTop: theme.spacing(8),display: 'flex', flexDirection: 'column', alignItems: 'center'}}>
              <Typography component="h1" variant="h2">
                CollegeSpace Chatroom
                </Typography>
                <form onSubmit={() => setclicked(true)}>
                <TextField
                  variant="outlined"
                  margin="normal"
                  required
                  fullWidth
                  id="email"
                  label="Chatroom Name"
                  name="Chatroom Name"
                  autoFocus
                  value={chatState.room}
                  onChange={e => {
                    setChatstate({ room: e.target.value });

                  }}
                />



                <Button
                  type="submit"
                  fullWidth
                  variant="contained"
                  color="primary"
                  className="Button"
                >
                  Start Chatting
                  </Button>
                </form>

              </div>
              </>}
          </Container>  
        </>,
        <>
          {/* {clicked !== true ? <div className='container-md' >
            <input type="text" placeholder='Enter Room Name' value={chatState.room} onChange={(e) => setChatstate({room : e.target.value()})} />
            <button className='btn btn-outline-success' type='submit' onClick={setclicked(true)}>Enter</button>
          </div>
          :
          <div className='container' style={{"margin" : "auto 0"}}>
                  {chatState.messages.map((item, index) => {
                        <p>{item}</p>
                  })}

          </div>} */}
        </>
    )

}

export default withStyles(useStyles)(Chat)
