import React, { useEffect, useState } from 'react'
import {useHistory} from 'react-router-dom'
import Navbar from './Navbar';
import Error from './Error';
import CreateQ from './CreateQ';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import EditorToolbar, { modules, formats } from "../EditorToolbar";

const QnA = () => {
    const BaseUrl = `http://127.0.0.1:8000`
    const history = useHistory();
    const [question, setQuestion] = useState([])
    const [clicked, setclicked] = useState(false);
    useEffect(() => {
        fetch(`${BaseUrl}${history.location.pathname}`)
        .then((data)=> data.json())
        .then((response)=> setQuestion(response)); 
    }, [setQuestion])



    return (
        <>
        <Navbar/>
        {typeof question[0] === "undefined" ? <>
            <Error/>
        </> : question[1].length !== 0 ? <div>
            <h4>
                {question.map((item, index)=> {
                    return(<>
                        <div key={index}>
                            <h4>{item.question}</h4>
                            <p style={{color : "#808080"}}>{item.question_date}</p>
                        </div>
                    </>)
                })}
            </h4>
            <hr/>
            {question[1].map((item, index) => {
                return(<>
                <div className='container'>
                    <p>{item.answer}</p>
                    <p>Answered on : {item.answered_date}</p>
                </div>
                </>
                )
            })}
        </div> : <>
        <div className='container' style={{width : "75%", marginTop:'5%'}}>
        <h4>
                {question.map((item, index)=> {
                    return(<>

                        <div key={index} style={{textAlign:"center"}}>
                            <h2>{item.question}</h2>
                            {/* <p >Asked on {item.question_date}</p> */}
                        </div>
                        <p>{item.user}</p>
                    </>)
                })}
            </h4>

            <hr/>

            <h5>No answer :-( .You haven't answered it yet</h5>
            <p>Can you answer it?</p>
            <button className='btn btn-success' onClick={() => setclicked(true)}>Answer it here</button>
            {clicked === true ? <>

        <div className='container' id="Editor" style={{width : "75%"}}> 
            <EditorToolbar toolbarId={'t1'}/>
            <ReactQuill
                theme="snow"

                placeholder={"Write something awesome..."}
                modules={modules('t1')}
                formats={formats}/>
                <button type='button' className='btn btn-outline-success'>Submit</button>
                <button type='submit' className='btn btn-outline-danger' onClick={() => setclicked(false)}>Cancel</button>
                </div>

            </>: <></>}
            </div>

         {/*Wanna make this form inside the button so that each time button is clicked, the form will appear there and will close down if 
         cancelled button is clicked :-)nmm */}   
        </>}
        </>
    )
}

export default QnA


