import React, { useEffect, useState } from 'react'
import {useHistory} from 'react-router-dom'
import Navbar from './Navbar';
import Error from './Error';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import EditorToolbar, { modules, formats } from "../EditorToolbar";
import CreateAns from './CreateAns';
import UpdateQ from './UpdateQ';
import UpdateAns from './UpdateAns';
const containerStyle = {"width": "50%", "height": "50%", "margin": "auto", "position": "absolute",
                        "top": '0',"left": '0', "bottom": '0', "right": '0'}
const QnA = () => {
    const BaseUrl = `http://127.0.0.1:8000`
    const history = useHistory();
    const [question, setQuestion] = useState([])
    const [ans, setans] = useState([])
    const [clicked, setclicked] = useState({clicked4ans : false, clicked4update : false, answer : false, upVal : null, clicked4ansUp : false, 
        userid : null, deleteAns : false});
    const getData = async () => {
        const data = await fetch(`${BaseUrl}${history.location.pathname}`)
        const resdata = await data.json()
        setQuestion(resdata)
        setans(resdata)
    }

    useEffect(() => {
        getData(); 
    }, [])
    // <UpdateAns clik={true} aid={id}/>
    return (
        <>
        <Navbar/>

        <div>
        {typeof question[0] === "undefined" ? <Error/> : 
            question.map((item, index)=> {
                    return(<>

                        <> {clicked.clicked4update === true ?
                        <UpdateQ q={item.question} qd={item.question_data} qid={item.id} qslug={item.slug} clickeed={clicked.clicked4update}/> : 
                        <>
                        <div className='container' style={{width : "50%","marginTop" : "5%", "textAlign" : "center"}}  key={index}>
                        <h4>{item.question}</h4>
                        <p style={{color : "#808080"}}>{item.question_date}</p>
                        {item.question !== undefined ? <>
                            <EditorToolbar className='Editor-1' toolbarId={'t1'}/>
                            <ReactQuill 
                            theme="bubble"
                            readOnly={true}
                            value={item.question_data}
                            modules={modules('t1')}
                            formats={formats}/> </>:<></>} 
                        {item.question !== undefined && item.user === (JSON.parse(atob(localStorage.getItem('access_token').split('.')[1])).user_id)
                        ? 
                        <><button type='button' className='btn btn-success' onClick={()=> setclicked({clicked4update:true})}>Update</button> 

                        </>: "" }
                        </div>
                        </>
                        }

                        </>   </>

                    )
                })}

        </div> 
        <hr style={{"width" : "50%", "textAlign" : "center"}}/>
        <h2 style={{textAlign : 'center', top:"20"}}>Answers</h2>
        <div className='container' style={{"alignItems" : 'center', "width" : "50%", marginTop: ""}}>

            {typeof ans[1] === "undefined" || ans[1].length === 0 ? 
            <div className='container'>
                <button type='btn' className='btn btn-success' onClick={() => setclicked({answer : true})} >Answer</button>
                {clicked.answer === true ? <CreateAns qid={(ans[0].id).toString()}/> : <h3>No answer Yet. Please Create it here</h3>}
            </div> :
            <div className='container' style={{"width": "90%", "height": "60%", "marginTop" : "10rem"}}>
            {ans[1].map((item, index) => {
               return( <>
               <div className='shadow p-3 mb-5 bg-white rounded' style={{width:"100%"}} key={index} >
                <EditorToolbar className="Editor-10" toolbarId={`t-${item.id}`}/>
                            <ReactQuill 
                            theme="bubble"
                            readOnly={true}
                            value={item.answer}
                            modules={modules(`t-${item.id}`)}
                            formats={formats}/>
                            <p>Answered on : {item.answered_date}</p>
            {item.user === (JSON.parse(atob(localStorage.getItem('access_token').split('.')[1])).user_id) ?
             <><button type='button' id={item.id} className='btn btn-primary' onClick={() => setclicked({upVal: item.id, clicked4ansUp : true, userid : item.user})}>
                    Update Answer
                </button>
                <button type='button' className='btn btn-danger' onClick={()=> setclicked({deleteAns:true})}>Delete</button>
                </>: <></> }
                </div>
                </> 
                )


            })}
    {clicked.clicked4ansUp === true ? <UpdateAns aid={clicked.upVal} show={true} qid={question[0].id} uid={clicked.userid}/> : <>{clicked.clicked4ansUp}</>}
    {clicked.deleteAns === true ? <UpdateAns aid={clicked.upVal} show={true} qid={question[0].id} uid={clicked.userid}/> : <>{clicked.clicked4ansUp}</>}
        </div>}
        </div>


         </>    
    )
}

export default QnA



