import React from 'react'
import Navbar from './Navbar'
import {BaseUrl} from './BaseUrl'
import {user} from './BaseUrl'
import { useEffect, useState } from 'react';
import Footer from './Footer';
import { Link } from 'react-router-dom';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import EditorToolbar, { modules, formats } from "../EditorToolbar";
import FlashMessage from 'react-flash-message'
import axiosInstance from '../AuthComp/AxiosInstance'
const Questions = () => {
    const [questions, setquestions]  = React.useState([])
    const [clicked, setClicked] = useState(false);
    useEffect(()=>{
        fetch(`${BaseUrl}questions`)
        .then((data) => data.json()).then((response)=> setquestions(response))}, [setquestions])


    const submitQ = () => {
        axiosInstance.post(`create-question`, {


        })
    }


    return (
        <>
        <Navbar/>
        <div className='container' style={{"width": "50%",
            "height": "50%",
            "margin": "auto",
            "position": "absolute",
            "top": '0',"left": '0', "bottom": '0', "right": '0'}}>
            <h1 style={{textAlign:"center"}}>Recently asked questions</h1>

            <button type='button' className='btn btn-outline-success' onClick={() => 
            {user !== false ? setClicked(true) :<>{alert("Please Login/Signup to Access this")}</>}    
                // axiosInstance.post(``, {

                // })
            }>Ask Question</button>
            {clicked === true ? 
                <>
                <EditorToolbar toolbarId={'t1'}/>
                    <ReactQuill theme="snow"
                        placeholder={"Write something awesome..."}
                        modules={modules('t1')}

                        formats={formats}
                        value={questions}
                        onChange={setquestions} />
                    <button type='button' className='btn btn-outline-success' onClick={submitQ}>Submit</button>
                    <button type='submit' className='btn btn-outline-danger' onClick={() => setClicked(false)}>Cancel</button>


            </>:
            <></>
         }
            <hr style={{"height":"2px", "width":"100%", "borderWidth":"0"}} />

            {/*style={{display: 'inline-block', width: "calc((100% - 0.1em)/2), "}}*/}
            <div className='container-md' style={{width:"50$"}}>
           {((questions.length) !== 0) ? questions.map((item, index)=> 
           {
               return(
                   <> 
                    <div key={index}>
                        <Link to={`/question/${item.slug}/${item.id}`}><h3>{item.question}</h3></Link>
                        <p>{item.question_date}</p>
                    </div>
                    </>
                    )
            })
           : <h4>Data not available right now</h4>}
           </div>

        </div>


        </>
    )
}

export default Questions
