import React, { useEffect, useState } from 'react'
import BaseUrl from './BaseUrl';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import EditorToolbar, { modules, formats } from "../EditorToolbar";
import {useHistory} from 'react-router-dom'

const UpdateQ = () => {
        const BaseUrl = `127.0.0.1:8000`
        const history = useHistory();
        const url = `${BaseUrl}${history.location.pathname}`
        //Set up the update method in both question and answer. 
        // Login and logout of a user 
        //checking if current user is the one updating/ writing the stuffs :-)
        const [prevData, setprevData] = useState({question : ''})
        useEffect(() => {
            fetch((url)).then((data) => data.json()).then((Response) => {console.log(Response) 
                setprevData({question : Response})})
        }, [setprevData])




    return (
        <div>
          <>

    <div className='container' id="Editor" style={{width : "75%"}}> 
        <EditorToolbar toolbarId={'t1'}/>
        <ReactQuill
            theme="snow"

            placeholder={"Write something awesome..."}
            modules={modules('t1')}
            formats={formats}/>
            <button type='button' className='btn btn-outline-success'>Submit</button>
            <button type='submit' className='btn btn-outline-danger'>Cancel</button>
            </div>

        </>  
        </div>
    )
}

export default UpdateQ
