import React, { useEffect, useState } from 'react'
import { BaseUrl } from '../BaseUrl'
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import EditorToolbar, { modules, formats } from '../../EditorToolbar';


const ViewAll = () => {
    const [notes, setAllnotes] = useState([])

    const getAllNotes = async () => {
        const data = await fetch(`${BaseUrl}view-notes`)
        const note = await data.json()
        setAllnotes(note)

    }
    useEffect(() => {
        getAllNotes();
    }, [setAllnotes])
    return (

        <div>
            {notes.length === 0 ? <h4>Oops. No notes here</h4> : 
            <>
            {notes.map((item, index) => {
                return(
                <>
                <h2  style={{"textAlign" : "center"}}></h2>
                <div className='container'>
                    <div className="row">
                            <div className="col-sm-3">
                                <div className="card">
                                <div className="card-body">
                                    <h5 key={index} className="card-title">{item.notes_title}</h5>

                                    <EditorToolbar toolbarId={`t-${item.id}`}/>
                                        <ReactQuill
                                            theme="bubble"
                                            readOnly={true}
                                            modules={modules(`t-${item.id}`)}
                                            formats={formats}
                                            value={item.notes_content}

                                            />
                                </div>
                                </div>
                            </div>
                </div>
                </div>
                </>
                )
            })}
            </>}
        </div>

    )
}

export default ViewAll
