import React, { useEffect, useState } from 'react'
import {useHistory} from 'react-router-dom'
import Navbar from './Navbar';
import Error from './Error';
import CreateQ from './CreateQ';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import EditorToolbar, { modules, formats } from "../EditorToolbar";

const QnA = () => {
    const BaseUrl = `http://127.0.0.1:8000`
    const history = useHistory();
    const [question, setQuestion] = useState([])
    useEffect(() => {
        fetch(`${BaseUrl}${history.location.pathname}`)
        .then((data)=> data.json())
        .then((response)=> setQuestion(response)); 
    }, [setQuestion])


    return (
        <>
        <Navbar/>
        {typeof question[0] === "undefined" ? <>
            <Error/>
        </> : question[1].length !== 0 ? <div>
            <h4>
                {question.map((item, index)=> {
                    return(<>
                        <div key={index}>
                            <h4>{item.question}</h4>
                            <p>{item.question_date}</p>
                        </div>
                    </>)
                })}
            </h4>
            <hr/>
            {question[1].map((item, index) => {
                return(<>
                <div className='container'>
                    <p>{item.answer}</p>
                    <p>Answered on : {item.answered_date}</p>
                </div>
                </>
                )
            })}
        </div> : <>
        <div className='container' style={{width : "75%", marginTop:'5%'}}>
        <h4>
                {question.map((item, index)=> {
                    return(<>

                        <div key={index}>
                            <h4>{item.question}</h4>
                            <p>{item.question_date}</p>
                        </div>
                        <p>{item.user}</p>
                    </>)
                })}
            </h4>
            </div>
            <hr style={{width:"50%", textAlign:"center"}}/>

            <h5>No answer :-( .You haven't answered it yet</h5>
            <p>Answer it here :-)</p>
            <div className='container' style={{width : "75%"}}> 
            <EditorToolbar toolbarId={'t1'}/>
            <ReactQuill
              theme="snow"

              placeholder={"Write something awesome..."}
              modules={modules('t1')}
              formats={formats}/></div>

        </>}
        </>
    )
}

export default QnA
