import React from 'react'
import Navbar from './Navbar'
import {BaseUrl} from './BaseUrl'
import {user} from './BaseUrl'
import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axiosInstance from '../AuthComp/AxiosInstance'
import 'react-draft-wysiwyg/dist/react-draft-wysiwyg.css';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import EditorToolbar, { modules, formats } from "../EditorToolbar";
const Questions = () => {
    const [questions, setquestions]  = React.useState([])
    const [questionD, setquestionD] = useState({
        question : "",
        addQuesData : ""
    });

    const [clicked, setClicked] = useState(false);
    useEffect(()=>{
        fetch(`${BaseUrl}questions`)
        .then((data) => data.json()).then((response)=> setquestions(response))}, [setquestions])




    const submitQ = () => {

        axiosInstance.post(`create-question`, {
            question : questionD,
            user : (JSON.parse(atob(localStorage.getItem('access_token').split('.')[1])).user_id) 
        }).then((res) => console.log(res));
        }

    return (
        <>
        <Navbar/>
        <div className='container' style={{"width": "50%",
            "height": "50%",
            "margin": "auto",
            "position": "absolute",
            "top": '0',"left": '0', "bottom": '0', "right": '0'}}>
            <h1 style={{textAlign:"center"}}>Recently asked questions</h1>

            <button type='button' className='btn btn-outline-success' onClick={() => 
            {user !== false ? setClicked(true) :<>{alert("Please Login/Signup to Access this")}</>}    
            }>Ask Question</button>
            {clicked === true ? 
                <>
                <form onChange={(e) => setquestionD(e.target.value)}>
                <div class="form-group">
                    <label for="exampleInputEmail1">Email address</label>
                    <input type="email" value={questionD.question} className="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Enter email"/>
                    <small id="emailHelp" className="form-text text-muted">Write your Main Question here & additional info below</small>
                    </div>
                </form>
                <>
                <div className='container' id="Editor" style={{width : "75%"}}> 
                    <EditorToolbar toolbarId={'t1'}/>
                    <ReactQuill
                        theme="snow"
                        placeholder={"Write something awesome..."}
                        modules={modules('t1')}
                        formats={formats}
                        value={questionD.addQuesData}
                        onChange={handleChange}
                        />
                    <button type='button' className='btn btn-outline-success'>Submit</button>
                    <button type='submit' className='btn btn-outline-danger' onClick={() => setClicked(false)}>Cancel</button>
                </div>

                </>
                <button type='button' className='btn btn-outline-success' onClick={submitQ}>Submit</button>
                    <button type='submit' className='btn btn-outline-danger' onClick={() => setClicked(false)}>Cancel</button>


            </>:
            <></>
         }
            <hr style={{"height":"2px", "width":"100%", "borderWidth":"0"}} />

            {/*style={{display: 'inline-block', width: "calc((100% - 0.1em)/2), "}}*/}
            <div className='container-md' style={{width:"50$"}}>
           {((questions.length) !== 0) ? questions.map((item, index)=> 
           {
               return(
                   <> 
                    <div key={index}>
                        <Link to={`/question/${item.slug}/${item.id}`}>

                        <h3>

                        {/* <div dangerouslySetInnerHTML={createMarkup(item.question)}></div> */}
                        <h4>{item.question}</h4>
                        </h3>

                        </Link>

                        <p>{item.question_date}</p>
                    </div>
                    </>
                    )
            })
           : <h4>Data not available right now</h4>}
           </div>




        </div>
        </>

    )
}

export default Questions
