import React, { useEffect, useState } from 'react'
import {useHistory} from 'react-router-dom'
import Navbar from './Navbar';
import Error from './Error';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import EditorToolbar, { modules, formats } from "../EditorToolbar";
import CreateAns from './CreateAns';
import UpdateQ from './UpdateQ';
const containerStyle = {"width": "50%", "height": "50%", "margin": "auto", "position": "absolute",
                        "top": '0',"left": '0', "bottom": '0', "right": '0'}
const QnA = () => {
    const BaseUrl = `http://127.0.0.1:8000`
    const history = useHistory();
    const [question, setQuestion] = useState([])
    const [ans, setans] = useState([])
    const [clicked, setclicked] = useState({clicked4ans : false, clicked4update : false, answer : false, isNull : true});
    const getData = async () => {
        const data = await fetch(`${BaseUrl}${history.location.pathname}`)
        const resdata = await data.json()
        setQuestion(resdata)
        setans(resdata)
    }

    useEffect(() => {
        getData(); 
    }, [])


    return (
        <>
        <Navbar/>

        <div>
        {typeof question[0] === "undefined" ? <Error/> : 
            question.map((item, index)=> {
                    return(<>

                        <> {clicked.clicked4update === true ?
                        <UpdateQ q={item.question} qd={item.question_data} qid={item.id} qslug={item.slug}/> : 
                        <>
                        <div className='container' style={{width : "50%","marginTop" : "5%", "textAlign" : "center"}}  key={index}>
                        <h4>{item.question}</h4>
                        <p style={{color : "#808080"}}>{item.question_date}</p>
                        {item.question !== undefined ? <>
                            <EditorToolbar toolbarId={'t1'}/>
                            <ReactQuill
                            theme="bubble"
                            readOnly={true}
                            value={item.question_data}
                            modules={modules('t1')}
                            formats={formats}/> </>:<></>} 
                        {item.question !== undefined && item.user === (JSON.parse(atob(localStorage.getItem('access_token').split('.')[1])).user_id)
                        ? 
                        <button type='button' className='btn btn-success' onClick={()=> setclicked({clicked4update:true})}>Update</button> : "" }
                        </div>
                        </>
                        }

                        </> 



                        </>

                    )
                })}

        </div> 
        <hr/>
        <h2 style={{textAlign : 'center', top:"20"}}>Answers</h2>
        <div className='container' style={{"alignItems" : 'center', "width" : "50%", marginTop: ""}}>

            {typeof ans[1] === "undefined" || ans[1].length === 0 ? <div className='container'><h3>No answer Yet. Please Create it here</h3> 
                <button type='btn' className='btn btn-success' onClick={() => <CreateAns/>} >Answer</button>
            </div> :
            <div className='container' style={{"width": "50%", "height": "50%", "marginTop" : "15rem"}}>
            {ans[1].map((item, index) => {
               return( <>
               <div className='shadow p-3 mb-5 bg-white rounded' >
                <p key={item.id}>{item.answer}</p>
                <p>Answered on : {item.answered_date}</p>
                {(item.status) === true ? <button type='button' className='btn btn-primary' onClick={() => <CreateAns/>} >
                    Update Answer
                </button>: <></> }
                </div>
                </>
                )
            })}
        </div>}
        </div>

        {/* typeof question.questions === "undefined" ? <Error/> : */}
        {/* {clicked.isNull === false ? 
            question[1].map((item, index) => {
                return(<>
                {item.status === true ? setclicked({answered : true}) : <></>}
                <div className='container'>
                    <p key={index}>{item.answer}</p>
                    <p>Answered on : {item.answered_date}</p>
                </div>
                </>
                )
            }):<>
            <h5>No answer .You haven't answered it yet</h5>
            <p>Can you answer it?</p>
            <button className='btn btn-success' onClick={() => setclicked({clicked4ans : true})}>Answer it here</button>
            {clicked.clicked4ans === true ? 
            <>
            <CreateAns/>
            <button type='submit' className='btn btn-outline-danger' onClick={() => setclicked({clicked4ans : false})}>Cancel</button>
           </>: <></>
           }
        </>
         }  */}
         </>    
    )
}

export default QnA

