import React, { useState } from 'react'
import { useEffect } from 'react'
import { BaseUrl } from '../BaseUrl'
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import EditorToolbar, { modules, formats } from '../../EditorToolbar';
const ViewUNotes = () => {

    const [notes, setusernotes] = useState([]);
    const u_id = JSON.parse(atob(localStorage.getItem('access_token').split('.')[1])).user_id
    const getUserData = async() => {
        const data = await fetch(`${BaseUrl}view-user-notes/${u_id}`)
        const note = await data.json()
        console.log(note)
        setusernotes(note)
    }
    {console.log(notes.length)}
    useEffect(() => {
        getUserData();    
    }, [setusernotes])
    return (
        <div className='row'>

        <div className='col-sm-3'>
        </div>
        <div className='col-md-6'>
        <div className='container'>

        {notes.length === 0 ? <h4>Oops. No notes here</h4> : 
            <>
            {notes.map((item, index) => {
                return(<div className='container'>
                <h4 key={index}>{item.notes_title}</h4>
                <hr style={{"width" : "70%", "margin" : "auto"}}/>
                <EditorToolbar toolbarId={'t2'}/>
                    <ReactQuill
                        theme="bubble"
                        readOnly={true}
                        modules={modules('t2')}
                        formats={formats}
                        value={item.notes_content}

                        />
                </div>)
            })}
            </>    
            }
</div>
        </div>
        </div>
    )
}

export default ViewUNotes
