import React, { useEffect, useState } from 'react'
import Navbar from '../Navbar'
import {useHistory} from 'react-router-dom'
import { BaseUrl } from '../BaseUrl'
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import EditorToolbar, { modules, formats } from '../../EditorToolbar';


const ViewSNote = () => {
   const  history = useHistory()
    const [notes, setnotes] = useState([])
    const [showupdate, setupdate] = useState(false)
    const getNotes = async () => {
        const data = await fetch(`${BaseUrl}view-notes${history.location.pathname}`)
        const note = await data.json()
        setnotes(note)
        }
    useEffect(() => {
        getNotes();
    }, [setnotes])

    return (
        <>
        <Navbar/>
        <div className='container' style={{"marginTop" : "3rem"}}>

        {typeof notes !== undefined ?
            <>
                <h2 key={notes.id} style={{"textAlign" : "center"}}>{notes.notes_title}</h2>
                <br/>
                <button className='btn btn-outline-success' style={{"marginLeft" : "50%","marginRight" : "50%"}} onClick={() => setupdate(true)}>Update</button>
                <p style={{"textAlign" : "right", "fontSize" : "10px", "marginRight" : "22rem"}}>Created on : {notes.created}</p>
                <hr style={{"width" : "70%", "margin" : "auto"}}/>

                <EditorToolbar toolbarId={'t2'}/>
                    <ReactQuill
                        theme="bubble"
                        readOnly={true}
                        modules={modules('t2')}
                        formats={formats}
                        value={notes.notes_content}
                        style={{"marginLeft" : "auto", "marginRight" : "auto", "marginTop" : "2rem", "width" : "70%", "fontFamily" : "serif", "fontSize" : "16px"}}
                    />
            </>
         : <h5>Can't fetch notes</h5>}
        </div>
        {}
        </>
    )
}
//not getting data in notes in valid form :-()
export default ViewSNote
