import React, { useEffect, useState } from 'react'
import {useHistory} from 'react-router-dom'
import Navbar from './Navbar';
import Error from './Error';
// import ReactQuill from 'react-quill';
// import 'react-quill/dist/quill.snow.css';
// import EditorToolbar, { modules, formats } from "../EditorToolbar";
// import UpdateQ from './UpdateQ';
import CreateAns from './CreateAns';

/*  if question undefined -- error 
else --> answer length not zero 
*/
const QnA = () => {
    const BaseUrl = `http://127.0.0.1:8000`
    const history = useHistory();
    const [question, setQuestion] = useState([])
    const [tryquestion, settryquestion] = useState([])

    const getData = async () => {
        const data = await fetch(`${BaseUrl}${history.location.pathname}`)
        const resdata = await data.json()
        setQuestion(resdata)
    }
    const [clicked, setclicked] = useState({clicked4ans : false, clicked4update : false, answered : false});
    useEffect(() => {
        getData(); 
    }, [])

    return (
        <>
        <Navbar/>
        { 
        <div>
        {question[0].map((item, index)=> {
                    return(<>
                        <div key={index}>
                        <>
                        <h2>{(item.user === (JSON.parse(atob(localStorage.getItem('access_token').split('.')[1])).user_id))}</h2>
                        <h4>{item.question}</h4>
                        <p style={{color : "#808080"}}>{item.question_date}</p>
                        {item.question !== undefined && item.user === (JSON.parse(atob(localStorage.getItem('access_token').split('.')[1])).user_id)
                          ? 
                            <button type='button' className='btn btn-success' onClick={()=> setclicked({clicked4update:true})}>Update</button> : "" }
                        </> 

                        </div>
                    </>
                    )
                })}

            <hr/>
        </div> 
        }
        {/* typeof question.questions === "undefined" ? <Error/> : */}
        {typeof question.answers !== "undefined" ? 
            question[1].map((item, index) => {
                return(<>
                {item.status === true ? setclicked({answered : true}) : <></>}
                <div className='container'>
                    <p key={index}>{item.answer}</p>
                    <p>Answered on : {item.answered_date}</p>
                </div>
                </>
                )
            }):<>
            <h5>No answer .You haven't answered it yet</h5>
            <p>Can you answer it?</p>
            <button className='btn btn-success' onClick={() => setclicked({clicked4ans : true})}>Answer it here</button>
            {clicked.clicked4ans === true ? 
            <>
            <CreateAns/>
            <button type='submit' className='btn btn-outline-danger' onClick={() => setclicked({clicked4ans : false})}>Cancel</button>
           </>: <></>
           }
        </>
         } 
         </>    
    )
}

export default QnA

