import React, {useEffect, useState} from 'react'
import { BaseUrl } from '../BaseUrl'
import Avatar from '@material-ui/core/Avatar';

const UserDet = (props) => {
    const [user, setUser] = useState([])

    useEffect(() => {
        fetch(`${BaseUrl}get_user/${props.uid}`).then((res) => res.json()).then((data)=> setUser(data))
}, [props.uid])
    return (
        <>
        {console.log(user)}
        {typeof user === undefined ? <>
            <h1>No data</h1>
        </> : <>
        <div>
        <h2 style={{"fontWeight" : "lighter"}}>About the Author</h2>
        <hr style={{"width" : "80%", "margin" : "auto"}}/>
        {console.log(user.first_name)}
        <Avatar>{user.first_name}</Avatar>        
        <h3>Name : {user.first_name} {" "} {user.last_name}</h3>
        <h4>Username : {user.username}</h4>

        </div>
        </>}
        </>
    )
}
// https://gomakethings.com/how-to-use-the-fetch-method-to-make-multiple-api-calls-with-vanilla-javascript/

export default UserDet
