import React, { useEffect, useState } from 'react'
import {useHistory} from 'react-router-dom'
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import EditorToolbar, { modules, formats } from '../../EditorToolbar';
//"../EditorToolbar"
import Modal from '@material-ui/core/Modal';

import axiosInstance from '../../AuthComp/AxiosInstance'
import {BaseUrl} from '../BaseUrl'
const UpdateN = (props) => {
    const history = useHistory();
    const [open, setOpen] = React.useState(props.show);
    const [note_c, setNotes] = useState(props.note_con)
    const [notes_t, setNote] = useState(props.note_t)
    const handleClose = () => {
        setOpen(false);
        window.location.reload();
      };


    const sendNotes = () => {
        axiosInstance.post(`${BaseUrl}update-note${history.location.pathname}`, {
            notes_title : notes_t,
            notes_content : note_c,
            user : props.user_id
        }).then((res) => {alert("Updated"); window.location.reload()}).catch((error) => console.log(error))
    }

    return (
        <div>
          <div style={{ display: 'block', padding: 30 }} className='shadow p-3 mb-5 bg-white rounded'>
            <Modal
                onClose={handleClose}
                open={open}
                style={{ position: 'absolute', border: "1px solid", boxShadow: '3px #888888', height:"80%", width: "70%", margin: 'auto',backgroundColor : "white"}}>
                <div className='container shadow p-3 mb-5 bg-white rounded' style={{"height" : "100%"}}>
                <h2 style={{"textAlign" : "center", "fontFamily" : "serif"}}>Update your Notes</h2>
                <br/>
            <form onSubmit={(e) => {e.preventDefault(); }}>

                <div className="form-group form-group-lg">
                    <label style={{"fontSize" : "10px"}} htmlFor="exampleInputPassword1">Title</label>
                    <input type="text" name='notes_title' style={{"height" : "30px"}} value={notes_t} onChange={(e) => setNote(e.target.value)}
                      className="form-control input-lg"
                     id="exampleInputPassword1" placeholder="Note Title"/>
                </div>
                </form>
                <br/>
                <h5>Notes Content</h5>
{/* value={} onChange={} */}
                <EditorToolbar className='Editor-1' toolbarId={'t9'}/>
                    <ReactQuill 
                    theme="snow"
                    modules={modules('t9')}
                    formats={formats}
                    style={{"height" : "42%", "width" : "100%"}}
                    value={note_c} onChange={(event) => setNotes(event)} 
                    />
                    <br/>

                <button className='btn btn-outline-primary' style={{"width" : "48%"}} onClick={sendNotes}>Update</button>
                <button className='btn btn-outline-danger' style={{"width" : "48%", "marginLeft" : "4%"}} onClick={handleClose}>Cancel</button>
                </div>

            </Modal>
    </div>  
        </div>
    )
}

export default UpdateN
