import React, { useState } from 'react'
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import axiosInstance from '../AuthComp/AxiosInstance';
import EditorToolbar, { modules, formats } from "../EditorToolbar";
import {useHistory} from 'react-router-dom'
const CreateAns = () => {
    const history = useHistory();
    const [answer, setAnswer] = useState(" ");
    const submitAnswer = () => {
        axiosInstance.post(`${history.location.pathname}` , {
            answer : answer,
            user : (JSON.parse(atob(localStorage.getItem('access_token').split('.')[1])).user_id)
        })
    }
    return (

        <div>
               {console.log("js")}
             <div className='container' id="Editor" style={{width : "75%"}}> 
                <EditorToolbar toolbarId={'t1'}/>
                <ReactQuill
                    theme="snow"
                    placeholder={"Write something awesome..."}
                    modules={modules('t1')}
                    value={answer}
                    onChange={(event) => setAnswer(event)}
                    formats={formats}/>
                    <button type='button' className='btn btn-outline-success' onClick={submitAnswer}>Submit</button>

            </div>
        </div>
    )
}

export default CreateAns
