    const [user, setUser] = useState("")
    const history = useHistory()

    useEffect(() => {
        fetch(`${BaseUrl}view-notes${history.location.pathname}`)
        .then((res) => res.json())
        .then((data)=> {
            const uid = data.user;
            return fetch(`${BaseUrl}get_user/${uid}`);
        }).then((response) => response.json()).then((udata) => setUser(udata))
