import React, { useState } from 'react'
import './signup.css'
import Navbar from '../Component/Navbar';


//Here by using useState true and false, we can have this sliding functionality here
const Login = () =>  {
    	const[inputtext,setinputtext]=useState({email:"", password:""});

		const[warnemail,setwarnemail]=useState(false);
		const[warnpassword,setwarnpassword]=useState(false);

		const[eye,seteye]=useState(true);
		const[password,setpassword]=useState("password");
		const[type,settype]=useState(false);

		const inputEvent=(event)=>{
		const name=event.target.name;
		const value=event.target.value;
		setinputtext((lastValue)=>{
		return{
		...lastValue,
		[name]:value}});
	}
		const submitForm=(e) => 
		{
			e.preventDefault();
				setwarnemail(false);
				setwarnpassword(false);
		if(inputtext.email==="")
		{
			setwarnemail(true);
		}
		else if(inputtext.password===""){
		setwarnpassword(true);
		}
		else{
		alert("form submitted");
		}

		}

		const Eye=()=>{
		if(password==="password"){
		setpassword("text");
		seteye(false);
		settype(true);
		}
		else{
		setpassword("password");
		seteye(true);
		settype(false);
		}
		}


		return(
		<>
			<div className="container">
				<div className="card">
					<div className="text">
						<h3>Welcome Back</h3>
						<p>Enter your credentials to access your account.</p>
					</div>
					<form onSubmit={submitForm}>
						<div className="input-text">
							<input type="text" className={` ${warnemail ? "warning" : "" }`} placeholder="Enter your email" value={inputtext.email} onChange={inputEvent} name="email" />
							<i className="fa fa-envelope"></i>

						</div>
						<div className="input-text">
							<input type={password} className={` ${warnpassword ? "warning" : "" } ${type ? "type_password" : "" }`} placeholder="Enter your password" value={inputtext.password} onChange={inputEvent} name="password" />
							<i className="fa fa-lock"></i>
							<i onClick={Eye} className={`fa ${eye ? "fa-eye-slash" : "fa-eye" }`}></i>
						</div>
						<div className="buttons">
							<button type="submit">Sign in</button>
						</div>
						<div className="forgot">
							<p>Forgot your password? <a href="#">Reset Password</a></p>
						</div>
					</form>
				</div>
			</div>

		</>

		);

}

export default Login
