import React from 'react'
import Navbar from './Navbar'
import {BaseUrl} from './BaseUrl'
import {user} from './BaseUrl'
import { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import axiosInstance from '../AuthComp/AxiosInstance'
import {EditorState, convertToRaw, convertFromRaw} from 'draft-js'
import { Editor } from 'react-draft-wysiwyg';
import draftToHtml from 'draftjs-to-html';
import DOMPurify from 'dompurify';
import 'react-draft-wysiwyg/dist/react-draft-wysiwyg.css';

const Questions = () => {
    const [questions, setquestions]  = React.useState([])
    const [clicked, setClicked] = useState(false);
    useEffect(()=>{
        fetch(`${BaseUrl}questions`)
        .then((data) => data.json()).then((response)=> setquestions(response))}, [setquestions])


    const  [convertedContent, setConvertedContent] = useState(null);

    const submitQ = () => {

        axiosInstance.post(`create-question`, {
            question : convertedContent,
            user : (JSON.parse(atob(localStorage.getItem('access_token').split('.')[1])).user_id) 
        }).then((res) => console.log(res));
        }
        const [editorState, setEditorState] = useState(
            () => EditorState.createEmpty(),
          );

        const handleEditorChange = (state) => {
            setEditorState(state);
            convertContentToHTML();
          }  
        const convertContentToHTML = () => {
            const h = (convertToRaw(editorState.getCurrentContent()));
            console.log(convertFromRaw(h));
              let currentContentAsHTML = draftToHtml(convertToRaw(editorState.getCurrentContent()));


              setConvertedContent(currentContentAsHTML);

          }
        const createMarkup = (html) => {
            return  {
              __html: DOMPurify.sanitize(html)
            }
          }

    console.log(convertedContent);          
    return (
        <>
        <Navbar/>
        <div className='container' style={{"width": "50%",
            "height": "50%",
            "margin": "auto",
            "position": "absolute",
            "top": '0',"left": '0', "bottom": '0', "right": '0'}}>
            <h1 style={{textAlign:"center"}}>Recently asked questions</h1>

            <button type='button' className='btn btn-outline-success' onClick={() => 
            {user !== false ? setClicked(true) :<>{alert("Please Login/Signup to Access this")}</>}    
            }>Ask Question</button>
            {clicked === true ? 
                <>
                <Editor
                editorState={editorState}
                onEditorStateChange={handleEditorChange}    
                wrapperClassName="wrapper-class"
                editorClassName="editor-class"
                toolbarClassName="toolbar-class"/>

                <button type='button' className='btn btn-outline-success' onClick={submitQ}>Submit</button>
                    <button type='submit' className='btn btn-outline-danger' onClick={() => setClicked(false)}>Cancel</button>


            </>:
            <></>
         }
            <hr style={{"height":"2px", "width":"100%", "borderWidth":"0"}} />

            {/*style={{display: 'inline-block', width: "calc((100% - 0.1em)/2), "}}*/}
            <div className='container-md' style={{width:"50$"}}>
           {((questions.length) !== 0) ? questions.map((item, index)=> 
           {
               return(
                   <> 
                    <div key={index}>
                        <Link to={`/question/${item.slug}/${item.id}`}>

                        <h3>

                        {/* <div dangerouslySetInnerHTML={createMarkup(item.question)}></div> */}
                        <h4>{item.question}</h4>
                        </h3>

                        </Link>

                        <p>{item.question_date}</p>
                    </div>
                    </>
                    )
            })
           : <h4>Data not available right now</h4>}
           </div>




        </div>
        </>

    )
}

export default Questions
