import React, { useEffect, useState } from 'react'
import BaseUrl from './BaseUrl';
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import EditorToolbar, { modules, formats } from "../EditorToolbar";


const UpdateQ = () => {
    const [prevData, setprevData] = useState({question: ""});
    const [newData, setNewData] = useState({question: ""});

    useEffect(() => {
    fetch(`${BaseUrl}update-question/<slug:slug>/<str:pk>`)
    .then((data)=> data.json())
    .then((response) => setprevData({question : response.question}));
    }, [setprevData]);
    const setData = () => {

    }
    return (
        <div>
          <>

    <div className='container' id="Editor" style={{width : "75%"}}> 
        <EditorToolbar toolbarId={'t1'}/>
        <ReactQuill
            theme="snow"
            value={prevData.question}
            onChange={setData}
            placeholder={"Write something awesome..."}
            modules={modules('t1')}
            formats={formats}/>
            <button type='button' className='btn btn-outline-success'>Submit</button>
            <button type='submit' className='btn btn-outline-danger' onClick={()=> setNewData("Aniket")}>Cancel</button>
            </div>

        </>  
        </div>
    )
}

export default UpdateQ
