import React, { useState } from 'react'
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import axiosInstance from '../AuthComp/AxiosInstance';
import EditorToolbar, { modules} from "../EditorToolbar";
import {useHistory} from 'react-router-dom'
const CreateAns = (props) => {
    const history = useHistory();
    const [answer, setAnswer] = useState({ answers : " "});
    const submitAnswer = () => {
        axiosInstance.post(`create-answer` , {
            answer : answer.answers,
            question : props.qid,
            user : (JSON.parse(atob(localStorage.getItem('access_token').split('.')[1])).user_id)
        }).then((res) => {console.log(res); history.push('/questions')})
    }

    const answerChange = (event) => setAnswer({ answers : event})
    console.log("Clicked")
    return (

        <>
             <div className='contain' style={{width : "75%"}}>
                <EditorToolbar toolbarId={'t2'}/>
                <ReactQuill
                    modules={modules('t2')}
                    value={answer.answers}
                    onChange={answerChange}
                    />

                    <button type='button' className='btn btn-outline-success' onClick={submitAnswer}>Submit</button> 

            </div>
        </>
    )
}

export default CreateAns
