import React, { useEffect, useState } from 'react'
import  Navbar  from '../Navbar'
import CreateN from './CreateN'
import ViewAll from './ViewAll'
import View_Notes from './View_Notes'
const ViewNotes = () => {
    const [showM, setShow] = useState({showCreate : false, showaLL: false});


    return (
        <>
        <Navbar/>
            <div className='container-md'style={{"marginTop" : "3rem"}} >
                <h2 style={{"textAlign" : "center"}}>Notes :-)</h2>
                <button className='btn btn-outline-success' style={{"display" : "flex"}} onClick={() => setShow({ showCreate : true})}>Create</button>
                <hr style={{"width":"50%", "margin": "auto"}}/>
                {showM.showCreate === true ? <CreateN show={true}/> : <></>}    
                <button type='btn btn-success' onClick={() => setShow({showaLL : false})}>Your Notes</button>
                <button type='btn btn-success' onClick={() => setShow({showaLL : true})}>All Notes</button>
                <div>
                {showM.showaLL === false ? <View_Notes/> : <ViewAll/>}
                </div>
            </div>

             {/*  tasks left are 1. update notes , delete notes, question , answer | view notes based on user choice, all his or all of others*/}
        </>
    )
}

export default ViewNotes
