import React, { useEffect, useState } from 'react'
import Navbar from '../Navbar'
import {useHistory} from 'react-router-dom'
import { BaseUrl } from '../BaseUrl'
import ReactQuill from 'react-quill';
import 'react-quill/dist/quill.snow.css';
import EditorToolbar, { modules, formats } from '../../EditorToolbar';
import UpdateN from './UpdateN';
import DeleteN from './DeleteN';
import Error from '../Error';
const ViewSNote = () => {
   const  history = useHistory()
    const [notes, setnotes] = useState([])
    const [showupdate, setupdate] = useState({update : false, delete : false, s : false})
    const getNotes = async () => {
        const data = await fetch(`${BaseUrl}view-notes${history.location.pathname}`)
        const note = await data.json()
        setnotes(note)
        }
    useEffect(() => {
        getNotes();
    }, [setnotes])

    return (
        <>
        <Navbar/>
        <div className='row'>
        <div className='col-sm-1'></div>
        <div className='col-lg-9'>
        <div className='container-md' style={{"marginTop" : "3rem", "width" : "80%"}}>

        {typeof notes !== undefined ?
            <>
                <h2 key={notes.id} style={{"textAlign" : "center"}}>{notes.notes_title}</h2>
                <br/>
                <p style={{"textAlign" : "center"}}>
                <button className='btn btn-outline-primary' style={{"width": "20%", "marginRight" : "10%"}} onClick={() => setupdate({update:true})}>Update</button>
                <button className='btn btn-outline-danger' style={{"width": "20%", "margin" : "auto"}} onClick={() => setupdate({ delete : true})}>Delete</button>
                </p>
                <p style={{"textAlign":"center"}}>Posted On : {Date(notes.created).substr(0,16)}</p>
                <hr style={{"width" : "70%", "margin" : "auto"}}/>

                <EditorToolbar toolbarId={'t2'}/>
                    <ReactQuill
                        theme="bubble"
                        readOnly={true}
                        modules={modules('t2')}
                        formats={formats}
                        value={notes.notes_content}
                        style={{"marginLeft" : "auto", "marginRight" : "auto", "marginTop" : "2rem", "width" : "70%", "fontFamily" : "serif", "fontSize" : "16px"}}
                    />
            </>
         : <h5><Error/></h5>}
        </div>
        </div>
        <div className='col-sm-2'>
            {console.log("hello")}
        </div>
        {showupdate.update === true ?<>
            <UpdateN note_t = {notes.notes_title} note_con={notes.notes_content} show={true} user_id={notes.user}/>
        </> :<></> }
        {showupdate.delete === true ?<>
            <DeleteN del={true} id={notes.id}/>
        </> :<></> }
        </div>
        </>
    )
}
//not getting data in notes in valid form :-()
export default ViewSNote
